import{_ as s,c as a,o as l,a as n}from"./app.99e343b5.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[{"level":2,"title":"1.1 准备所需的工具","slug":"_1-1-准备所需的工具","link":"#_1-1-准备所需的工具","children":[]},{"level":2,"title":"1.2 安装及配置VM虚拟机","slug":"_1-2-安装及配置vm虚拟机","link":"#_1-2-安装及配置vm虚拟机","children":[]},{"level":2,"title":"1.3 安装Linux系统","slug":"_1-3-安装linux系统","link":"#_1-3-安装linux系统","children":[{"level":3,"title":"1.3.1 以gpt格式安装系统","slug":"_1-3-1-以gpt格式安装系统","link":"#_1-3-1-以gpt格式安装系统","children":[]}]},{"level":2,"title":"1.4  设置启动模式为纯文本模式","slug":"_1-4-设置启动模式为纯文本模式","link":"#_1-4-设置启动模式为纯文本模式","children":[]},{"level":2,"title":"1.5  重置root密码","slug":"_1-5-重置root密码","link":"#_1-5-重置root密码","children":[]}],"relativePath":"RHEL8/chapter_01.md"}'),e={name:"RHEL8/chapter_01.md"},t=n(`<h2 id="_1-1-准备所需的工具" tabindex="-1">1.1 准备所需的工具 <a class="header-anchor" href="#_1-1-准备所需的工具" aria-hidden="true">#</a></h2><ol><li>VMware Workstation Pro</li><li>SecureCRT + SecureFX</li></ol><h2 id="_1-2-安装及配置vm虚拟机" tabindex="-1">1.2 安装及配置VM虚拟机 <a class="header-anchor" href="#_1-2-安装及配置vm虚拟机" aria-hidden="true">#</a></h2><h2 id="_1-3-安装linux系统" tabindex="-1">1.3 安装Linux系统 <a class="header-anchor" href="#_1-3-安装linux系统" aria-hidden="true">#</a></h2><h3 id="_1-3-1-以gpt格式安装系统" tabindex="-1">1.3.1 以gpt格式安装系统 <a class="header-anchor" href="#_1-3-1-以gpt格式安装系统" aria-hidden="true">#</a></h3><ol><li><p>上下键选择【 Install Red Hat Enterprise Linux 8 】选项</p></li><li><p>按下【 Table 】键</p></li><li><p>光标移至最后追加如下参数：</p></li></ol><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">inst.gpt</span></span>
<span class="line"></span></code></pre></div><h2 id="_1-4-设置启动模式为纯文本模式" tabindex="-1">1.4 设置启动模式为纯文本模式 <a class="header-anchor" href="#_1-4-设置启动模式为纯文本模式" aria-hidden="true">#</a></h2><p>在命令行终端下，运行以下命令</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">systemctl</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">set-default</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">multi-user.target</span></span>
<span class="line"></span></code></pre></div><h2 id="_1-5-重置root密码" tabindex="-1">1.5 重置root密码 <a class="header-anchor" href="#_1-5-重置root密码" aria-hidden="true">#</a></h2><ol><li><p>重启Linux系统，在引导选择界面，按下 e 键到内核编辑界面</p></li><li><p>在linux参数这行的最后追加 “rd.break” 参数，然后按下 Ctrl +X 组合键来运行修改过的内核程序</p></li><li><p>依次输入以下命令，等待系统重启完毕，然后就可以使用新的密码来登录Linux系统了</p></li></ol><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki material-theme-palenight" tabindex="0"><code><span class="line"><span style="color:#FFCB6B;">mount</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">-o</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">remount,rw</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/sysroot</span><span style="color:#A6ACCD;">    </span><span style="color:#C3E88D;">以读写模式重新挂载根分区</span></span>
<span class="line"><span style="color:#FFCB6B;">chroot</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/sysroot</span><span style="color:#A6ACCD;">              </span><span style="color:#C3E88D;">切换到系统真正的根目录下sysroot（/）</span></span>
<span class="line"><span style="color:#FFCB6B;">passwd</span><span style="color:#A6ACCD;">                           </span><span style="color:#C3E88D;">重置密码</span></span>
<span class="line"><span style="color:#FFCB6B;">touch</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">/.autorelabel</span><span style="color:#A6ACCD;">         </span><span style="color:#C3E88D;">这将导致整个文件系统被“重新标记”，在下次重新启动时向</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">SELinux</span><span style="color:#A6ACCD;"> </span><span style="color:#C3E88D;">发出信号，表明文件系统已更改（更改的密码）并允许加载更改</span></span>
<span class="line"><span style="color:#82AAFF;">exit</span></span>
<span class="line"><span style="color:#FFCB6B;">reboot</span></span>
<span class="line"></span></code></pre></div>`,13),o=[t];function p(r,i,c,d,h,C){return l(),a("div",null,o)}const y=s(e,[["render",p]]);export{u as __pageData,y as default};
