import{_ as e,c as l,a0 as r,o as n}from"./chunks/framework.CGHvQLJz.js";const o=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"RHEL8/chapter_05.md","filePath":"RHEL8/chapter_05.md"}'),d={name:"RHEL8/chapter_05.md"};function a(i,t,s,c,x,y){return n(),l("div",null,t[0]||(t[0]=[r('<p>Vim编辑器与Shell命令脚本</p><h2 id="_1-vim-文本编辑器" tabindex="-1">(1) <code>Vim</code> 文本编辑器 <a class="header-anchor" href="#_1-vim-文本编辑器" aria-label="Permalink to &quot;(1) `Vim` 文本编辑器&quot;">​</a></h2><p>三种模式：</p><ul><li>命令模式</li><li>输入模式</li><li>末行模式</li></ul><p>命令模式中最常用的一些命令</p><table tabindex="0"><thead><tr><th style="text-align:center;">命令</th><th style="text-align:center;">作用</th></tr></thead><tbody><tr><td style="text-align:center;">dd</td><td style="text-align:center;">删除(剪切)光标所在整行</td></tr><tr><td style="text-align:center;">5dd</td><td style="text-align:center;">删除(剪切)从光标处开始的5行</td></tr><tr><td style="text-align:center;">yy</td><td style="text-align:center;">复制光标所在整行</td></tr><tr><td style="text-align:center;">5yy</td><td style="text-align:center;">复制从光标处开始的5行</td></tr><tr><td style="text-align:center;">n</td><td style="text-align:center;">显示搜索命令定位到的下一个字符串</td></tr><tr><td style="text-align:center;">N</td><td style="text-align:center;">显示搜索命令定位到的上一个字符串</td></tr><tr><td style="text-align:center;">u</td><td style="text-align:center;">撤销上一步的操作</td></tr><tr><td style="text-align:center;">p</td><td style="text-align:center;">将之前删除（dd）或复制（yy）过的数据黏贴到光标后面</td></tr></tbody></table><p>末行模式中常用的一些命令</p><table tabindex="0"><thead><tr><th style="text-align:center;">命令</th><th style="text-align:center;">作用</th></tr></thead><tbody><tr><td style="text-align:center;">:w</td><td style="text-align:center;">保存</td></tr><tr><td style="text-align:center;">:q</td><td style="text-align:center;">退出</td></tr><tr><td style="text-align:center;">:q!</td><td style="text-align:center;">强制不保存退出</td></tr><tr><td style="text-align:center;">:wq!</td><td style="text-align:center;">强制保存退出</td></tr><tr><td style="text-align:center;">:set nu</td><td style="text-align:center;">显示行号</td></tr><tr><td style="text-align:center;">:set nonu</td><td style="text-align:center;">不显示行号</td></tr><tr><td style="text-align:center;">:命令</td><td style="text-align:center;">执行该命令</td></tr><tr><td style="text-align:center;">:整数</td><td style="text-align:center;">跳转到该行</td></tr><tr><td style="text-align:center;">:/s/one/two</td><td style="text-align:center;">将当前光标所在行的第一个one替换成two</td></tr><tr><td style="text-align:center;">:/s/one/two/g</td><td style="text-align:center;">将当前光标所在行的所有one替换成two</td></tr><tr><td style="text-align:center;">:%s/one/two/g</td><td style="text-align:center;">将全文中的所有one替换成two</td></tr><tr><td style="text-align:center;">?字符串</td><td style="text-align:center;">在文本中从下至上搜索该字符串</td></tr><tr><td style="text-align:center;">/字符串</td><td style="text-align:center;">在文本中从上至下搜索该字符串</td></tr></tbody></table><h2 id="_2-编写-shell-脚本" tabindex="-1">(2)编写 Shell 脚本 <a class="header-anchor" href="#_2-编写-shell-脚本" aria-label="Permalink to &quot;(2)编写 Shell 脚本&quot;">​</a></h2><p>Shell 脚本命令的工作方式：</p><ul><li>交互式</li><li>批处理</li></ul><p>第一行的脚本声明（#!）用来告诉系统使用那种解释器来执行该脚本</p><p>执行脚本两种方式：</p><ol><li><p>bash xxx.sh</p></li><li><p>chmod u+x xxx sh</p><p>./xxx.sh</p></li></ol><h2 id="_3-计划任务服务程序" tabindex="-1">(3)计划任务服务程序 <a class="header-anchor" href="#_3-计划任务服务程序" aria-label="Permalink to &quot;(3)计划任务服务程序&quot;">​</a></h2><p>计划任务分类：</p><ul><li>一次性计划任务</li><li>长期性计划任务</li></ul><p>at 命令中的参数及其作用</p><table tabindex="0"><thead><tr><th style="text-align:center;">参数</th><th style="text-align:center;">作用</th></tr></thead><tbody><tr><td style="text-align:center;">-f</td><td style="text-align:center;">指定包含命令的文件</td></tr><tr><td style="text-align:center;">-q</td><td style="text-align:center;">指定新任务的名称</td></tr><tr><td style="text-align:center;">-l</td><td style="text-align:center;">显示待执行任务的列表</td></tr><tr><td style="text-align:center;">-d</td><td style="text-align:center;">删除指定的待执行任务</td></tr><tr><td style="text-align:center;">atrm</td><td style="text-align:center;">删除指定的待执行任务</td></tr><tr><td style="text-align:center;">-m</td><td style="text-align:center;">任务执行后向用户发送邮件</td></tr></tbody></table><p>编写任务的方法：ctrl+d 或 echo配合管道符</p><p>crontab 命令中的参数及其作用</p><table tabindex="0"><thead><tr><th style="text-align:center;">参数</th><th style="text-align:center;">作用</th></tr></thead><tbody><tr><td style="text-align:center;">-e</td><td style="text-align:center;">编辑计划任务</td></tr><tr><td style="text-align:center;">-u</td><td style="text-align:center;">指定用户名称</td></tr><tr><td style="text-align:center;">-l</td><td style="text-align:center;">列出任务列表</td></tr><tr><td style="text-align:center;">-r</td><td style="text-align:center;">删除计划任务</td></tr></tbody></table>',22)]))}const h=e(d,[["render",a]]);export{o as __pageData,h as default};
